$p = Read-Host -Prompt "Base64 Payload"; $d = [System.Convert]::FromBase64String($p); $io = New-Object System.IO.MemoryStream(,$d); $gz = New-Object System.IO.Compression.GZipStream($io, [System.IO.Compression.CompressionMode]::Decompress); $r = New-Object System.IO.StreamReader($gz, [System.Text.Encoding]::UTF8); $c = $r.ReadToEnd(); $r.Dispose(); $gz.Dispose(); $io.Dispose(); iex $c;
